                    <p style={{ fontSize: "1.5rem", fontWeight: "bold" }}>
                      ${project.budget?.remaining?.toLocaleString() || "0"}
                    </p>
                  </div>
                </div>
              </div>

              {/* Budget Allocation */}
              {project.budget?.breakdown &&
                Object.keys(project.budget.breakdown).length > 0 && (
                  <div style={{ marginBottom: "2rem" }}>
                    <h4 style={{ fontWeight: "600", marginBottom: "1rem" }}>
                      Budget Allocation
                    </h4>
                    <div
                      style={{
                        backgroundColor: "#f9fafb",
                        padding: "1.5rem",
                        borderRadius: "8px",
                      }}
                    >
                      {Object.entries(project.budget.breakdown).map(
                        ([category, percentage], idx) => (
                          <div
                            key={idx}
                            style={{
                              display: "flex",
                              justifyContent: "space-between",
                              alignItems: "center",
                              padding: "0.75rem 0",
                              borderBottom:
                                idx <
                                Object.entries(project.budget.breakdown)
                                  .length -
                                  1
                                  ? "1px solid #e5e7eb"
                                  : "none",
                            }}
                          >
                            <span>{category}</span>
                            <div
                              style={{
                                display: "flex",
                                alignItems: "center",
                                gap: "1rem",
                              }}
                            >
                              <span
                                style={{ fontWeight: "600", color: "#2563eb" }}
                              >
                                {percentage}%
                              </span>
                              <span style={{ color: "#6b7280" }}>
                                ($
                                {Math.round(
                                  ((project.budget.total || 0) * percentage) /
                                    100
                                ).toLocaleString()}
                                )
                              </span>
                            </div>
                          </div>
                        )
                      )}
                    </div>
                  </div>
                )}

              {/* Channel Budgets */}
              {project.channelStrategy &&
                Object.keys(project.channelStrategy).length > 0 && (
                  <div>
                    <h4 style={{ fontWeight: "600", marginBottom: "1rem" }}>
                      Channel Budgets
                    </h4>
                    <div style={{ display: "grid", gap: "1rem" }}>
                      {Object.entries(project.channelStrategy).map(
                        ([channel, strategy], idx) => (
                          <div
                            key={idx}
                            style={{
                              padding: "1rem",
                              backgroundColor: "#f9fafb",
                              borderRadius: "8px",
                              display: "flex",
                              justifyContent: "space-between",
                              alignItems: "center",
                            }}
                          >
                            <div>
                              <h5
                                style={{
                                  fontWeight: "500",
                                  marginBottom: "0.25rem",
                                }}
                              >
                                {channel}
                              </h5>
                              {strategy.description && (
                                <p
                                  style={{
                                    fontSize: "0.875rem",
                                    color: "#6b7280",
                                  }}
                                >
                                  {strategy.description}
                                </p>
                              )}
                            </div>
                            {strategy.budget && (
                              <div style={{ textAlign: "right" }}>
                                <p
                                  style={{
                                    fontSize: "1.25rem",
                                    fontWeight: "600",
                                    color: "#2563eb",
                                  }}
                                >
                                  ${strategy.budget.toLocaleString()}
                                </p>
                              </div>
                            )}
                          </div>
                        )
                      )}
                    </div>
                  </div>
                )}
            </div>
          )}

          {/* Metrics Tab */}
          {activeTab === "metrics" && (
            <div>
              <h3
                style={{
                  fontSize: "1.5rem",
                  fontWeight: "bold",
                  marginBottom: "1.5rem",
                }}
              >
                Performance Metrics
              </h3>

              {project.metrics && project.metrics.length > 0 ? (
                <div style={{ display: "grid", gap: "1rem" }}>
                  {project.metrics.map((metric, idx) => (
                    <div
                      key={idx}
                      style={{
                        padding: "1.5rem",
                        backgroundColor: "#f9fafb",
                        borderRadius: "8px",
                        display: "flex",
                        justifyContent: "space-between",
                        alignItems: "center",
                      }}
                    >
                      <div>
                        <h4
                          style={{ fontWeight: "600", marginBottom: "0.25rem" }}
                        >
                          {metric.metric}
                        </h4>
                        <p style={{ fontSize: "0.875rem", color: "#6b7280" }}>
                          {metric.category} â€¢ {metric.measurement}
                        </p>
                        {metric.timeline && (
                          <p
                            style={{
                              fontSize: "0.75rem",
                              color: "#9ca3af",
                              marginTop: "0.25rem",
                            }}
                          >
                            Timeline: {metric.timeline}
                          </p>
                        )}
                      </div>
                      <div style={{ textAlign: "right" }}>
                        <p
                          style={{
                            fontSize: "1.5rem",
                            fontWeight: "bold",
                            color: "#2563eb",
                          }}
                        >
                          {metric.target}
                        </p>
                        <p style={{ fontSize: "0.875rem", color: "#6b7280" }}>
                          Target
                        </p>
                        {metric.current !== undefined && (
                          <p
                            style={{
                              fontSize: "0.75rem",
                              color: "#10b981",
                              marginTop: "0.25rem",
                            }}
                          >
                            Current: {metric.current}
                          </p>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p style={{ color: "#6b7280" }}>
                  No metrics defined for this campaign.
                </p>
              )}

              {/* Expected Outcomes */}
              {project.expectedOutcomes &&
                project.expectedOutcomes.length > 0 && (
                  <div style={{ marginTop: "2rem" }}>
                    <h4 style={{ fontWeight: "600", marginBottom: "1rem" }}>
                      Expected Outcomes
                    </h4>
                    <div
                      style={{
                        display: "grid",
                        gridTemplateColumns:
                          "repeat(auto-fit, minmax(250px, 1fr))",
                        gap: "1rem",
                      }}
                    >
                      {project.expectedOutcomes.map((outcome, idx) => (
                        <div
                          key={idx}
                          style={{
                            backgroundColor: "#fef3c7",
                            padding: "1.25rem",
                            borderRadius: "8px",
                            borderLeft: "4px solid #f59e0b",
                          }}
                        >
                          <h5
                            style={{
                              fontWeight: "600",
                              marginBottom: "0.5rem",
                              color: "#92400e",
                            }}
                          >
                            {outcome.metric}
                          </h5>
                          <p
                            style={{
                              fontSize: "1.25rem",
                              fontWeight: "bold",
                              color: "#78350f",
                            }}
                          >
                            {outcome.target}
                          </p>
                          {outcome.measurement && (
                            <p
                              style={{
                                fontSize: "0.75rem",
                                color: "#b45309",
                                marginTop: "0.25rem",
                              }}
                            >
                              {outcome.measurement}
                            </p>
                          )}
                        </div>
                      ))}
                    </div>
                  </div>
                )}
            </div>
          )}

          {/* Channels Tab */}
          {activeTab === "channels" && (
            <div>
              <h3
                style={{
                  fontSize: "1.5rem",
                  fontWeight: "bold",
                  marginBottom: "1.5rem",
                }}
              >
                Channel Strategy
              </h3>

              {project.channelStrategy &&
              Object.keys(project.channelStrategy).length > 0 ? (
                <div style={{ display: "grid", gap: "1rem" }}>
                  {Object.entries(project.channelStrategy).map(
                    ([channel, strategy], idx) => (
                      <div
                        key={idx}
                        style={{
                          padding: "1.5rem",
                          backgroundColor: "#f9fafb",
                          borderRadius: "8px",
                          border: "1px solid #e5e7eb",
                        }}
                      >
                        <h4
                          style={{
                            fontWeight: "600",
                            marginBottom: "0.75rem",
                            display: "flex",
                            alignItems: "center",
                            gap: "0.5rem",
                          }}
                        >
                          <Target
                            style={{
                              width: "18px",
                              height: "18px",
                              color: "#2563eb",
                            }}
                          />
                          {channel}
                        </h4>

                        {strategy.description && (
                          <p style={{ color: "#6b7280", marginBottom: "1rem" }}>
                            {strategy.description}
                          </p>
                        )}

                        {strategy.objective && (
                          <div style={{ marginBottom: "0.75rem" }}>
                            <strong style={{ fontSize: "0.875rem" }}>
                              Objective:
                            </strong>
                            <p
                              style={{
                                fontSize: "0.875rem",
                                color: "#6b7280",
                                marginTop: "0.25rem",
                              }}
                            >
                              {strategy.objective}
                            </p>
                          </div>
                        )}

                        {strategy.tactics && strategy.tactics.length > 0 && (
                          <div style={{ marginBottom: "0.75rem" }}>
                            <strong style={{ fontSize: "0.875rem" }}>
                              Tactics:
                            </strong>
                            <ul
                              style={{
                                marginLeft: "1.5rem",
                                marginTop: "0.25rem",
                                fontSize: "0.875rem",
                                color: "#6b7280",
                              }}
                            >
                              {strategy.tactics.map((tactic, tacticIdx) => (
                                <li key={tacticIdx}>{tactic}</li>
                              ))}
                            </ul>
                          </div>
                        )}

                        {strategy.budget && (
                          <div style={{ marginBottom: "0.75rem" }}>
                            <strong style={{ fontSize: "0.875rem" }}>
                              Budget:
                            </strong>
                            <span
                              style={{
                                marginLeft: "0.5rem",
                                color: "#2563eb",
                                fontWeight: "600",
                              }}
                            >
                              ${strategy.budget.toLocaleString()}
                            </span>
                          </div>
                        )}

                        {strategy.metrics && strategy.metrics.length > 0 && (
                          <div>
                            <strong style={{ fontSize: "0.875rem" }}>
                              Success Metrics:
                            </strong>
                            <ul
                              style={{
                                marginLeft: "1.5rem",
                                marginTop: "0.25rem",
                                fontSize: "0.875rem",
                                color: "#6b7280",
                              }}
                            >
                              {strategy.metrics.map((metric, metricIdx) => (
                                <li key={metricIdx}>{metric}</li>
                              ))}
                            </ul>
                          </div>
                        )}
                      </div>
                    )
                  )}
                </div>
              ) : (
                <p style={{ color: "#6b7280" }}>
                  No channel strategy defined for this campaign.
                </p>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
