        <div
          style={{
            position: "fixed",
            inset: 0,
            backgroundColor: "rgba(0, 0, 0, 0.5)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 50,
            padding: "16px",
          }}
        >
          <div
            style={{
              backgroundColor: "white",
              borderRadius: "12px",
              boxShadow: "0 25px 50px -12px rgba(0, 0, 0, 0.25)",
              width: "100%",
              maxWidth: "48rem",
              maxHeight: "85vh",
              overflow: "hidden",
            }}
          >
            <div
              style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "flex-start",
                padding: "24px",
                borderBottom: "1px solid #e5e7eb",
              }}
            >
              <div style={{ flex: 1 }}>
                <h3
                  style={{
                    fontSize: "20px",
                    fontWeight: "600",
                    color: "#111827",
                  }}
                >
                  {selectedTemplate.name}
                </h3>
                <p
                  style={{
                    fontSize: "14px",
                    color: "#4b5563",
                    marginTop: "4px",
                  }}
                >
                  {selectedTemplate.description}
                </p>
                {selectedTemplate.aiScore && (
                  <div
                    style={{
                      marginTop: "8px",
                      display: "flex",
                      alignItems: "center",
                      gap: "8px",
                    }}
                  >
                    <Sparkles
                      style={{
                        width: "16px",
                        height: "16px",
                        color: "#7c3aed",
                      }}
                    />
                    <span
                      style={{
                        fontSize: "14px",
                        fontWeight: "500",
                        color: "#6d28d9",
                      }}
                    >
                      AI Score: {selectedTemplate.aiScore}%
                    </span>
                  </div>
                )}
              </div>
              <button
                onClick={() => setSelectedTemplate(null)}
                style={{
                  color: "#9ca3af",
                  padding: "8px",
                  borderRadius: "8px",
                  border: "none",
                  backgroundColor: "transparent",
                  cursor: "pointer",
                }}
                onMouseOver={(e) => {
                  e.currentTarget.style.color = "#4b5563";
                  e.currentTarget.style.backgroundColor = "#f3f4f6";
                }}
                onMouseOut={(e) => {
                  e.currentTarget.style.color = "#9ca3af";
                  e.currentTarget.style.backgroundColor = "transparent";
                }}
              >
                <X style={{ width: "20px", height: "20px" }} />
              </button>
            </div>

            <div
              style={{ padding: "24px", overflowY: "auto", maxHeight: "55vh" }}
            >
              {/* AI Insights */}
              {selectedTemplate.aiInsights &&
                selectedTemplate.aiInsights.length > 0 && (
                  <div
                    style={{
                      marginBottom: "16px",
                      backgroundColor: "#f3e8ff",
                      borderRadius: "8px",
                      padding: "16px",
                    }}
                  >
                    <h4
                      style={{
                        fontWeight: "600",
                        color: "#5b21b6",
                        marginBottom: "8px",
                        display: "flex",
                        alignItems: "center",
                        gap: "8px",
                      }}
                    >
                      <Bot style={{ width: "16px", height: "16px" }} />
                      AI Template Analysis
                    </h4>
                    <ul
                      style={{
                        margin: 0,
                        paddingLeft: "20px",
                        fontSize: "14px",
                        color: "#6d28d9",
                      }}
                    >
                      {selectedTemplate.aiInsights.map((insight, idx) => (
                        <li key={idx} style={{ marginBottom: "4px" }}>
                          {insight}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}

              {/* Template Preview */}
              <div
                style={{
                  backgroundColor: "#f9fafb",
                  border: "1px solid #e5e7eb",
                  borderRadius: "8px",
                  padding: "32px",
                  textAlign: "center",
                  marginBottom: "16px",
                }}
              >
                <div style={{ fontSize: "60px", marginBottom: "16px" }}>
                  {selectedTemplate.fileType === "word"
                    ? "📄"
                    : selectedTemplate.fileType === "powerpoint"
                    ? "📊"
                    : selectedTemplate.fileType === "excel"
                    ? "📈"
                    : selectedTemplate.fileType === "email"
                    ? "📧"
                    : "📝"}
                </div>
                <h4
                  style={{
                    fontWeight: "600",
                    color: "#111827",
                    marginBottom: "8px",
                  }}
                >
                  {selectedTemplate.name}
                </h4>
                <p
                  style={{
                    color: "#4b5563",
                    fontSize: "14px",
                    marginBottom: "16px",
                  }}
                >
                  File size: {selectedTemplate.fileSize}
                </p>
                <div
                  style={{
                    display: "flex",
                    justifyContent: "center",
                    gap: "12px",
                  }}
                >
                  <button
                    onClick={() => {
                      alert(`Downloading ${selectedTemplate.name}...`);
                      // Update use count
                      setTemplates((prev) =>
                        prev.map((t) =>
                          t.id === selectedTemplate.id
                            ? {
                                ...t,
                                useCount: t.useCount + 1,
                                lastUsed: new Date().toISOString(),
                              }
                            : t
                        )
                      );
                    }}
                    style={{
                      display: "flex",
                      alignItems: "center",
                      gap: "8px",
                      backgroundColor: "#4f46e5",
                      color: "white",
                      padding: "8px 16px",
                      borderRadius: "8px",
                      border: "none",
                      cursor: "pointer",
                      fontSize: "14px",
                      transition: "background-color 0.2s",
                    }}
                    onMouseOver={(e) =>
                      (e.currentTarget.style.backgroundColor = "#4338ca")
                    }
                    onMouseOut={(e) =>
                      (e.currentTarget.style.backgroundColor = "#4f46e5")
                    }
                  >
                    <Download style={{ width: "16px", height: "16px" }} />
                    Download Template
                  </button>
                  <button
                    style={{
                      display: "flex",
                      alignItems: "center",
                      gap: "8px",
                      backgroundColor: "#f3f4f6",
                      color: "#374151",
                      padding: "8px 16px",
                      borderRadius: "8px",
                      border: "none",
                      cursor: "pointer",
                      fontSize: "14px",
                      transition: "background-color 0.2s",
                    }}
                    onMouseOver={(e) =>
                      (e.currentTarget.style.backgroundColor = "#e5e7eb")
                    }
                    onMouseOut={(e) =>
                      (e.currentTarget.style.backgroundColor = "#f3f4f6")
                    }
                  >
                    <Eye style={{ width: "16px", height: "16px" }} />
                    Preview
                  </button>
                </div>
              </div>

              {/* Template Details */}
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  gap: "12px",
                }}
              >
                <div
                  style={{
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "space-between",
                    fontSize: "14px",
                  }}
                >
                  <span style={{ color: "#4b5563" }}>Category</span>
                  <span
                    style={{
                      padding: "4px 12px",
                      borderRadius: "9999px",
                      fontSize: "12px",
                      fontWeight: "500",
                      backgroundColor:
                        selectedTemplate.category === "Presentation"
                          ? "#f3e8ff"
                          : selectedTemplate.category === "Document"
                          ? "#dbeafe"
                          : selectedTemplate.category === "Report"
                          ? "#d1fae5"
                          : selectedTemplate.category === "Form"
                          ? "#fef3c7"
                          : "#f3f4f6",
                      color:
                        selectedTemplate.category === "Presentation"
                          ? "#7c3aed"
                          : selectedTemplate.category === "Document"
                          ? "#1e40af"
                          : selectedTemplate.category === "Report"
                          ? "#047857"
                          : selectedTemplate.category === "Form"
                          ? "#b45309"
                          : "#374151",
                    }}
                  >
                    {selectedTemplate.category}
                  </span>
                </div>
                <div
                  style={{
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "space-between",
                    fontSize: "14px",
                  }}
                >
                  <span style={{ color: "#4b5563" }}>Created</span>
                  <span style={{ color: "#111827" }}>
                    {new Date(selectedTemplate.created).toLocaleDateString()}
                  </span>
                </div>
                <div
                  style={{
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "space-between",
                    fontSize: "14px",
                  }}
                >
                  <span style={{ color: "#4b5563" }}>Last Used</span>
                  <span style={{ color: "#111827" }}>
                    {new Date(selectedTemplate.lastUsed).toLocaleDateString()}
                  </span>
                </div>
                <div
                  style={{
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "space-between",
                    fontSize: "14px",
                  }}
                >
                  <span style={{ color: "#4b5563" }}>Total Uses</span>
                  <span style={{ fontWeight: "500", color: "#111827" }}>
                    {selectedTemplate.useCount}
                  </span>
                </div>
              </div>

              {/* Tags */}
              <div style={{ marginTop: "16px" }}>
                <h5
                  style={{
                    fontSize: "14px",
                    fontWeight: "500",
                    color: "#374151",
                    marginBottom: "8px",
                  }}
                >
                  Tags
                </h5>
                <div style={{ display: "flex", flexWrap: "wrap", gap: "8px" }}>
                  {selectedTemplate.tags.map((tag) => (
                    <span
                      key={tag}
                      style={{
                        backgroundColor: "#f3f4f6",
                        color: "#374151",
                        padding: "6px 12px",
                        borderRadius: "9999px",
                        fontSize: "14px",
                      }}
                    >
                      #{tag}
                    </span>
                  ))}
                </div>
              </div>
            </div>

            <div
              style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                padding: "24px",
                borderTop: "1px solid #e5e7eb",
                backgroundColor: "#f9fafb",
              }}
            >
              <div style={{ display: "flex", gap: "8px" }}>
                <button
                  onClick={() => {
                    alert("Opening template editor...");
                    setSelectedTemplate(null);
                    setShowTemplates(false);
                  }}
                  style={{
                    display: "flex",
                    alignItems: "center",
                    gap: "8px",
                    backgroundColor: "#4f46e5",
                    color: "white",
                    padding: "8px 16px",
                    borderRadius: "8px",
                    border: "none",
                    cursor: "pointer",
                    transition: "background-color 0.2s",
                  }}
                  onMouseOver={(e) =>
                    (e.currentTarget.style.backgroundColor = "#4338ca")
                  }
                  onMouseOut={(e) =>
                    (e.currentTarget.style.backgroundColor = "#4f46e5")
                  }
                >
                  Use Template
                </button>
                <button
                  style={{
                    display: "flex",
                    alignItems: "center",
                    gap: "8px",
                    padding: "8px 16px",
                    color: "#374151",
                    backgroundColor: "white",
                    border: "1px solid #e5e7eb",
                    borderRadius: "8px",
                    cursor: "pointer",
                    transition: "all 0.2s",
                  }}
                  onMouseOver={(e) =>
                    (e.currentTarget.style.backgroundColor = "#f9fafb")
                  }
                  onMouseOut={(e) =>
                    (e.currentTarget.style.backgroundColor = "white")
                  }
                >
                  <Copy style={{ width: "16px", height: "16px" }} />
                  Duplicate
                </button>
              </div>
              <button
                onClick={() => setSelectedTemplate(null)}
                style={{
                  padding: "8px 16px",
                  color: "#4b5563",
                  backgroundColor: "transparent",
                  border: "none",
                  cursor: "pointer",
                  transition: "color 0.2s",
                }}
                onMouseOver={(e) => (e.currentTarget.style.color = "#111827")}
                onMouseOut={(e) => (e.currentTarget.style.color = "#4b5563")}
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Add keyframes for animations */}
      <style
        dangerouslySetInnerHTML={{
          __html: `
        @keyframes spin {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }
        @keyframes pulse {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.5; }
        }
      `,
        }}
      />
    </div>
  );
};

export default MemoryVault;
