                }}
              ></div>
              <div>
                <div style={{ fontWeight: "600", color: "#6b21a8" }}>
                  Claude AI Processing
                </div>
                <div style={{ color: "#9333ea", fontSize: "14px" }}>
                  {homeResearchTopic}...
                </div>
                {activeProject && (
                  <div
                    style={{
                      color: "#9333ea",
                      fontSize: "12px",
                      marginTop: "4px",
                    }}
                  >
                    Project: {activeProject.projectName}
                  </div>
                )}
              </div>
            </div>
          </div>
        )}

        {/* Error State */}
        {researchError && !isResearching && (
          <div
            style={{
              marginBottom: "32px",
              backgroundColor: "#fef2f2",
              border: "1px solid #fecaca",
              borderRadius: "8px",
              padding: "24px",
              maxWidth: "64rem",
              margin: "0 auto 32px auto",
            }}
          >
            <div
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                gap: "12px",
              }}
            >
              <AlertCircle
                style={{ width: "24px", height: "24px", color: "#dc2626" }}
              />
              <div>
                <div style={{ fontWeight: "600", color: "#991b1b" }}>
                  AI Assistant Error
                </div>
                <div style={{ color: "#dc2626", fontSize: "14px" }}>
                  {researchError}
                </div>
                <button
                  onClick={() => {
                    setResearchError(null);
                    if (homeInputRef.current) {
                      let originalQuery = homeResearchTopic;
                      if (originalQuery.startsWith("Writing: ")) {
                        originalQuery = originalQuery.replace("Writing: ", "");
                      } else if (originalQuery.startsWith("Brainstorming: ")) {
                        originalQuery = originalQuery.replace(
                          "Brainstorming: ",
                          ""
                        );
                      } else if (originalQuery.startsWith("Editing: ")) {
                        originalQuery = originalQuery.replace("Editing: ", "");
                      }
                      homeInputRef.current.value = originalQuery;
                      homeInputRef.current.focus();
                    }
                  }}
                  style={{
                    marginTop: "8px",
                    color: "#b91c1c",
                    textDecoration: "underline",
                    fontSize: "14px",
                    background: "none",
                    border: "none",
                    cursor: "pointer",
                  }}
                >
                  Try again
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Platform Capabilities Section */}
        {!researchResults && !isResearching && !researchError && (
          <>
            <div style={{ marginTop: "64px" }}>
              <h3
                style={{
                  fontSize: "28px",
                  fontWeight: "bold",
                  color: "#1f2937",
                  marginBottom: "32px",
                }}
              >
                Platform Capabilities
              </h3>
              <div
                style={{
                  display: "grid",
                  gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
                  gap: "24px",
                  maxWidth: "64rem",
                  margin: "0 auto",
                }}
              >
                {[
                  {
                    icon: (
                      <Package
                        style={{
                          width: "40px",
                          height: "40px",
                          color: "white",
                        }}
                      />
                    ),
                    title: "Smart Project Context",
                    desc: "AI detects projects automatically",
                    gradient:
                      "linear-gradient(to bottom right, #3b82f6, #06b6d4)",
                  },
                  {
                    icon: (
                      <Brain
                        style={{
                          width: "40px",
                          height: "40px",
                          color: "white",
                        }}
                      />
                    ),
                    title: "Claude AI Assistant",
                    desc: "General research, writing, editing & brainstorming",
                    gradient:
                      "linear-gradient(to bottom right, #a855f7, #ec4899)",
                  },
                  {
                    icon: (
                      <Target
                        style={{
                          width: "40px",
                          height: "40px",
                          color: "white",
                        }}
                      />
                    ),
                    title: "Goal-Based Workflows",
                    desc: "Tell us your goal, we help you achieve it",
                    gradient:
                      "linear-gradient(to bottom right, #10b981, #14b8a6)",
                  },
                  {
                    icon: (
                      <Sparkles
                        style={{
                          width: "40px",
                          height: "40px",
                          color: "white",
                        }}
                      />
                    ),
                    title: "AI-Powered Everything",
                    desc: "Writing, editing, ideation & analysis",
                    gradient:
                      "linear-gradient(to bottom right, #f97316, #ef4444)",
                  },
                ].map((item, idx) => (
                  <div
                    key={idx}
                    style={{
                      backgroundColor: "white",
                      borderRadius: "16px",
                      padding: "24px",
                      boxShadow:
                        "0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)",
                      textAlign: "left",
                      transition: "all 0.3s",
                      cursor: "pointer",
                    }}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.boxShadow =
                        "0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)";
                      e.currentTarget.style.transform = "translateY(-4px)";
                    }}
                    onMouseLeave={(e) => {
                      e.currentTarget.style.boxShadow =
                        "0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)";
                      e.currentTarget.style.transform = "translateY(0)";
                    }}
                  >
                    <div
                      style={{
                        display: "inline-block",
                        padding: "12px",
                        borderRadius: "12px",
                        background: item.gradient,
                        marginBottom: "16px",
                      }}
                    >
                      {item.icon}
                    </div>
                    <h4
                      style={{
                        fontWeight: "600",
                        color: "#1f2937",
                        fontSize: "18px",
                        marginBottom: "8px",
                      }}
                    >
                      {item.title}
                    </h4>
                    <p
                      style={{
                        color: "#6b7280",
                        fontSize: "14px",
                        lineHeight: "1.5",
                      }}
                    >
                      {item.desc}
                    </p>
                  </div>
                ))}
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Homepage;
