      recommendedAction = 'Mitigate Risk';
    } else if (opportunities.length > 0 && risks.length > 0) {
      recommendedAction = 'Balanced Approach';
    }

    return {
      opportunities,
      risks,
      sentiment,
      prScore,
      recommendedAction,
      summary: {
        totalOpportunities: opportunities.length,
        totalRisks: risks.length,
        highPriorityOpportunities: opportunities.filter(o => o.priority === 'high').length,
        criticalRisks: risks.filter(r => r.priority === 'critical').length,
        dominantSentiment: sentiment.sentiment,
        actionableItems: opportunities.length + risks.length
      }
    };
  }

  /**
   * Monitor multiple content sources for PR signals
   */
  async monitorSources(sources, callback) {
    const results = [];

    for (const source of sources) {
      try {
        // In production, this would fetch real content from the source
        // For now, we'll simulate with the source description
        const analysis = this.analyzePRSignals(source.content || source.description || '', {
          sourceType: source.type,
          sourceName: source.name,
          isOwnBrand: source.isOwnBrand || false,
          isCompetitor: source.isCompetitor || false,
          isClient: source.isClient || false
        });

        results.push({
          source: source.name,
          timestamp: new Date().toISOString(),
          ...analysis
        });

        if (callback) {
          callback(analysis, source);
        }
      } catch (error) {
        console.error(`Error analyzing source ${source.name}:`, error);
      }
    }

    return results;
  }

  /**
   * Generate PR alert based on detection
   */
  generateAlert(detection, source) {
    const alert = {
      id: `alert-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
      timestamp: new Date().toISOString(),
      source: source.name,
      type: detection.type,
      category: detection.category,
      priority: detection.priority,
      title: this.generateAlertTitle(detection),
      description: this.generateAlertDescription(detection),
      actions: detection.type === 'opportunity' ? 
        [detection.actionRequired] : 
        [detection.mitigation],
      timeframe: detection.type === 'opportunity' ? 
        detection.timeframe : 
        detection.responseTime,
      metadata: {
        matches: detection.matches,
        score: detection.score || detection.severityScore,
        confidence: detection.confidence || detection.severity
      }
    };

    return alert;
  }

  /**
   * Generate alert title
   */
  generateAlertTitle(detection) {
    const titles = {
      opportunity: {
        funding: 'üí∞ New Funding Opportunity Detected',
        leadership: 'üëî Leadership Change - Engagement Opportunity',
        expansion: 'üåç Market Expansion Opportunity',
        product: 'üöÄ Product Launch PR Opportunity',
        crisis: '‚ö° Competitor Crisis - Positioning Opportunity',
        awards: 'üèÜ Award Win - Amplification Opportunity',
        partnership: 'ü§ù Partnership Announcement Opportunity',
        ipo: 'üìà IPO Communications Opportunity'
      },
      risk: {
        negative_sentiment: 'üò† Negative Sentiment Rising',
        regulatory: '‚öñÔ∏è Regulatory Risk Detected',
        competitor_attack: 'üéØ Competitive Threat Identified',
        internal_issues: 'üè¢ Internal Issue Requiring Management',
        financial: 'üí∏ Financial Risk Alert',
        reputation: 'üõ°Ô∏è Reputation Risk Detected',
        security: 'üîí Security Issue - Crisis Response Needed',
        social_media: 'üì± Social Media Crisis Developing'
      }
    };

    return titles[detection.type]?.[detection.category] || 
           `${detection.type === 'opportunity' ? 'üìç' : '‚ö†Ô∏è'} PR ${detection.type} Detected`;
  }

  /**
   * Generate alert description
   */
  generateAlertDescription(detection) {
    if (detection.type === 'opportunity') {
      return `A ${detection.priority} priority PR opportunity has been identified in the ${detection.category} category. ` +
             `Confidence level: ${Math.round(detection.confidence * 100)}%. ` +
             `Recommended response timeframe: ${detection.timeframe}.`;
    } else {
      return `A ${detection.priority} priority PR risk has been detected in the ${detection.category} category. ` +
             `Severity level: ${Math.round(detection.severity * 100)}%. ` +
             `Required response time: ${detection.responseTime}.`;
    }
  }
}

