// Flatten sources for easy use
const flattenSources = sourcesObj => {
  const flattened = [];
  for (const [category, sources] of Object.entries(sourcesObj)) {
    sources.forEach(source => {
      flattened.push({
        ...source,
        category,
        id: `${source.name.replace(/\s+/g, '-').toLowerCase()}-${Date.now()}`,
        active: true,
        isPreIndexed: true
